<script>
  //import moment from 'moment';
  import { mapGetters, mapActions } from 'vuex';
  import Datepicker from 'vuejs-datepicker';
  import {sr} from 'vuejs-datepicker/dist/locale';
  import Loading from '@/components/utils/Loading.vue';
  import ActionButtons from '@/components/utils/ActionButtons.vue';

  export default {
    name: 'Client',

    components: {
      Loading, Datepicker, ActionButtons
    },

    data() {
      return {
        enterClient: false,
        sr: sr,
        schedule: '',
        clientAttendances: [],
        clientInput: {
          //_id: '',
          first_name: '',
          last_name: '',
          email: '',
          phone: '',
          mobile: '',
          address: '',
          date_started: new Date,
          notes: '',
          picture: '',
          active: ''
        },
        appeared: false,
        notificationSystem: {
        options: {
          show: {
            theme: "dark",
            icon: "icon-person",
            position: "topCenter",
            progressBarColor: "rgb(0, 255, 184)",
         /*    buttons: [
              [
                "<button>Ok</button>",
                function(instance, toast) {
                  alert("Hello world!");
                },
                true
              ],
              [
                "<button>Close</button>",
                function(instance, toast) {
                  instance.hide(
                    {
                      transitionOut: "fadeOutUp",
                      onClosing: function(instance, toast, closedBy) {
                        console.info("closedBy: " + closedBy);
                      }
                    },
                    toast,
                    "buttonName"
                  );
                }
              ]
            ],
            onOpening: function(instance, toast) {
              console.info("callback abriu!");
            },
            onClosing: function(instance, toast, closedBy) {
              console.info("closedBy: " + closedBy);
            } */
          },
          ballon: {
            balloon: true,
            position: "bottomCenter"
          },
          info: {
            position: "bottomLeft"
          },
          success: {
            position: "center"
          },
          warning: {
            position: "center"
          },
          error: {
            position: "center"
          },
          question: {
            timeout: 20000,
            close: false,
            overlay: true,
            toastOnce: true,
            id: "question",
            zindex: 999,
            position: "center",
            buttons: [
              [
                "<button><b>YES</b></button>",
                function(instance, toast) {
                  instance.hide({ transitionOut: "fadeOut" }, toast, "button");
                },
                true
              ],
              [
                "<button>NO</button>",
                function(instance, toast) {
                  instance.hide({ transitionOut: "fadeOut" }, toast, "button");
                }
              ]
            ],
            onClosing: function(instance, toast, closedBy) {
              console.info("Closing | closedBy: " + closedBy);
            },
            onClosed: function(instance, toast, closedBy) {
              console.info("Closed | closedBy: " + closedBy);
            }
          }
        }
      }
      }
    },

    computed: {
      ...mapGetters([ 'getAllClients', 
                      'getOneClient',
                      'getAllSchedules',
                      'getAllAttendances',
                      'getErrors',
                      'loadingState' ]),
    },

    methods: {
      ...mapActions([ 'clientAdd', 
                      'clientUpdate', 
                      'clientDelete',
                      'fetchSchedules',
                      'fetchAttendances',
                      'formTypeChange',
                      'clearErrors',
                      'setLoadingState' ]),
      
      async addClient() {
        this.setLoadingState(true);
        if (this.getOneClient._id) {
          await this.clientUpdate(this.clientInput);
        } else {
          await this.clientAdd(this.clientInput);
        }
        this.setLoadingState(false);
        if (this.getErrors.length) {
          this.$toast.error('Greška! ' + this.getErrors, 'OK', this.notificationSystem.options.error);
          this.clearErrors();
        } else {
          this.$toast.success('Uspješno sačuvano!', 'OK', this.notificationSystem.options.success);
          this.formTypeChange('clients');
        }
      },

      async delClient() {
        await this.clientDelete(this.getOneClient);
        this.$toast.success('Uspješno obrisano!', 'OK', this.notificationSystem.options.success);
        this.formTypeChange('clients');
      },

      mapAttendances() {
        let obj, arr = []
        for (let i=0; i<this.getAllAttendances.length; i++) {
          for(let j =0; j<this.getAllAttendances[i].members.length; j++) {
            obj = {
              "client": this.getAllAttendances[i].members[j].client
            }
            arr.push(obj)
          }
        }
        return arr
      },

      mapSchedules() {
        let obj, arr = []
        for (let i=0; i<this.getAllSchedules.length; i++) {
          for(let j =0; j<this.getAllSchedules[i].members.length; j++) {
            obj = {
              "title": this.getAllSchedules[i].title,
              "startTime": this.getAllSchedules[i].startTime,
              "client": this.getAllSchedules[i].members[j].client
            }
            arr.push(obj)
          }
        }
        return arr
      },

      async newClient() {
        this.setLoadingState(true);
        this.formTypeChange('clientactivity');
        //await this.clientClear();
      },
      
      onAppeared() {
        this.appeared = true;
      }
    },

    async mounted() {
      if (this.getOneClient) {
        this.clientInput = this.getOneClient;
        if (!this.getOneClient.date_started) {
          this.clientInput.date_started = new Date;
        }
        await this.fetchSchedules();
        this.schedule = this.mapSchedules().filter(post => {
          return post.client._id == this.getOneClient._id
        });
        await this.fetchAttendances();
        this.clientAttendances = this.mapAttendances().filter(post => {
          return post.client._id == this.getOneClient._id
        });
      } 
      this.setLoadingState(false);
    },

  }
</script>

