</template>

<script>
  import moment from 'moment';
  import { mapGetters, mapActions } from 'vuex';

  const customLabels = {
    first: '<<',
    last: '>>',
    previous: '<',
    next: '>'
  };

  const customStyles = {
    ul: {
        //border: '2px solid red'
    },
    li: {
        display: 'inline-block',
        //border: '2px dotted green'
    },
    a: {
        color: 'var(--purple-dark)'
    }
  };

  export default {
    name: 'Clients',

    data() {
      return {
        pageOfItems: [],
        filteredClients: [],
        customLabels,
        customStyles,
        search: '',
        appeared: false
      }
    },

    computed: {
      ...mapGetters([ 'getAllClients', 
                      'loadingState' ]),

      // filteredList() {
/*         this.getAllClients.filter(post => {
          return post.name.toLowerCase().includes(this.search.toLowerCase())
        })  */
        
/*         let mu = this.pageOfItems.filter(post => {
          return post.name.toLowerCase().includes(this.search.toLowerCase()) || 
                  post.email.toLowerCase().includes(this.search.toLowerCase()) || 
                  post.mobile.includes(this.search)
        }) */
        //this.pageOfItems = mu
        // return mu
      // }
    },

    methods: {
      ...mapActions([ 'fetchClients', 
                      'fetchClient',
                      'clientClear',
                      'formTypeChange',
                      'setLoadingState' ]),

      onChangePage(pageOfItems) {
        // update page of items
        this.pageOfItems = pageOfItems;
      },
      
      searchClients() {
        console.log('iiiii')
        let mu = this.getAllClients.filter(post => {
          return post.name.toLowerCase().includes(this.search.toLowerCase()) || 
                  post.email.toLowerCase().includes(this.search.toLowerCase()) || 
                  post.mobile.includes(this.search)
        })
        this.filteredClients = mu
      },

      async newClient() {
        this.setLoadingState(true);
        this.formTypeChange('client');
        await this.clientClear();
      },

      async selectClient(client) {
        this.setLoadingState(true);
        await this.fetchClient(client);
        this.formTypeChange('client');
      },

      async clientActivities(client) {
        this.setLoadingState(true);
        await this.fetchClient(client);
        this.formTypeChange('clientactivity');
      },

      onAppeared() {
        this.appeared = true;
      }
    },

    filters: {
      formatDate: function(value) {
        if (value) {
          return moment(String(value)).format('dddd, LL')
        }
      }
    },

    async mounted() {
      await this.fetchClients();
      this.filteredClients = this.getAllClients;
      this.setLoadingState(false);
    }
  }
</script>

