      <div class="">
        <div class="days__list">
          <span>Trošak</span>
          <span>Datum</span>
          <span>Iznos</span>
          <span>Napomena</span>
        </div>
        <div v-for="expense in pageOfItems" :key="expense._id" 
              @click="selectExpense(expense)" class="clients__list expense__list">
          <p class="client__item">{{ expense.expense_title }}</p>
          <p class="client__item">{{ expense.expense_date | formatDate }}</p>
          <p class="client__item">{{ expense.expense_amount }}</p>
          <p class="client__item">{{ expense.notes }}</p>
        </div>
      </div>
    </div>
    <jw-pagination :items="getAllExpenses" @changePage="onChangePage" :labels="customLabels" :styles="customStyles"></jw-pagination>
  </div>
</template>

<script>
  const customLabels = {
    first: '<<',
    last: '>>',
    previous: '<',
    next: '>'
  };

  const customStyles = {
    ul: {
        //border: '2px solid red'
    },
    li: {
        display: 'inline-block',
        //border: '2px dotted green'
    },
    a: {
        color: 'var(--purple-dark)'
    }
  };

  import moment from 'moment';
  import { mapGetters, mapActions } from 'vuex';
  export default {
    name: 'Expenses',

    data() {
      return {
        pageOfItems: [],
        customLabels,
        customStyles
      }
    },

    computed: {
      ...mapGetters([ 'getAllExpenses',
                      'loadingState' ]),
    },

    filters: {
      formatDate: function(value) {
        if (value) {
          return moment(String(value)).format('DD/MM/YYYY')
        }
      }
    },

    methods: {
      ...mapActions([ 'fetchExpenses', 
                      'fetchExpense',
                      'expenseClear',
                      'formTypeChange',
                      'setLoadingState' ]),

      onChangePage(pageOfItems) {
        // update page of items
        this.pageOfItems = pageOfItems;
      },
      
      async newExpense() {
        this.setLoadingState(true);
        this.formTypeChange('expense');
        await this.expenseClear();
      },

      async selectExpense(expense) {
        this.setLoadingState(true);
        await this.fetchExpense(expense);
        this.formTypeChange('expense');
      },

      mapExpenses(expense) {
        return expense.reduce((a, expense_amount) => a + expense_amount, 0);
      },
    },

    async mounted() {
      await this.fetchExpenses();
      this.setLoadingState(false);
    }
  }
</script>

<style>
  .expense__list {
    grid-template-columns: repeat(4, 1fr) !important;
  }
</style>